import React, { Component } from "react";
import ReactDOM from "react-dom";

const e = React.createElement;
var acc = document.getElementById("acc");
var fn = document.getElementById("fn");
var ln = document.getElementById("ln");
var ea = document.getElementById("ea");
var pw = document.getElementById("pw");
var pw1 = document.getElementById("pw1");
var sq = document.getElementById("sq");
var sa = document.getElementById("sa");
var pn = document.getElementById("pn");

let data = {
  firstname: "",
  lastname: "",
  email: "",
  phone: "",
  role: "",
  password: "",
  securityquestion: "",
  answer: ""
};

class LikeButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = { liked: false };
  }

  render() {
    if (this.state.liked) {
      acc.innerHTML = JSON.stringify(data);
      //go to log in page
      this.send();
      alert("Account created successfully!");
    }

    return e("button", { onClick: () => this.handleClick() }, "Register");
  }

  send() {
    if (
      /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(this.state.email)
    ) {
      const Http = new XMLHttpRequest();
      const url = "http://localhost:3000";
      Http.open("POST", url);
      Http.setRequestHeader("Content-Type", "application/json");
      Http.send(JSON.stringify(data));
    } else {
      alert("Please use a valid email address to register.");
    }
  }

  handleClick() {
    if (String(pw.value) != String(pw1.value)) {
      alert("Passwords don't match!");
    }

    if (
      String(fn.value) == "" ||
      String(ln.value) == "" ||
      String(ea.value) == ""
    ) {
      alert("Please Fill all blank!");
    }

    if (
      String(fn.value) == "" ||
      String(ln.value) == "" ||
      String(ea.value) == "" ||
      String(pw.value) == "" ||
      String(sa.value) == ""
    ) {
      alert("Please Fill all blank!");
    }

    if (String(pw.value).length <= 12) {
      alert("Passwords too short!");
    } else {
      this.setState({ liked: true });
      data.firstname = String(fn.value);
      data.lastname = String(ln.value);
      data.email = String(ea.value);
      data.password = String(pw.value);
      data.securityquestion = String(sq.value);
      data.answer = String(sa.value);
      data.role = "T";
      data.phone = String(pn.value);
    }
  }
}

export default LikeButton;

